<?php











namespace Composer;

use Composer\Semver\VersionParser;






class InstalledVersions
{
private static $installed = array (
  'root' => 
  array (
    'pretty_version' => 'dev-master',
    'version' => 'dev-master',
    'aliases' => 
    array (
    ),
    'reference' => '5eb149c827078ac22bcb81122933edfe2e4c4ab8',
    'name' => '__root__',
  ),
  'versions' => 
  array (
    '__root__' => 
    array (
