      'version' => 'dev-master',
      'aliases' => 
      array (
      ),
      'reference' => '5eb149c827078ac22bcb81122933edfe2e4c4ab8',
    ),
    'yosymfony/parser-utils' => 
    array (
      'pretty_version' => 'v2.0.0',
      'version' => '2.0.0.0',
      'aliases' => 
      array (
